let parts = message.replace('create task:', '').split(',');
return [
    {
        title: parts[0]?.trim() || '',
        description: parts[1]?.trim() || '',
        assignee: parts[2]?.trim() || '',
        status: 'Creada'
    }
];

let tasks = $input.first().json.map(task => `ID: ${task.id}
Title: ${task.title}
Status: ${task.status}
Assignee: ${task.assignee}

`);
return [
    {
        message: `Tasks in the system:
${tasks.join('')}`
    }
];

let message = $input.first().json.body?.message || $input.first().json.message || '';
let parts = message.replace('change status of task:', '').split(',');
return [
    {
        id: parts[0]?.trim() || '',
        status: parts[1]?.trim() || ''
    }
];

// Analizar las tareas y preparar contexto inteligente para DoBot
const tasks = $input.first().json || [];
const userMessage = $('DoBot AI Chat Trigger').first().json.body?.message || $('DoBot AI Chat Trigger').first().json.chatInput || '';

// Estad√≠sticas detalladas
const stats = {
  total: tasks.length,
  completed: tasks.filter(t => t.status?.toLowerCase().includes('complet') || t.status?.toLowerCase().includes('done')).length,
  pending: tasks.filter(t => t.status?.toLowerCase().includes('creada') || t.status?.toLowerCase().includes('progress')).length,
  blocked: tasks.filter(t => t.status?.toLowerCase().includes('blocked') || t.status?.toLowerCase().includes('bloqueada')).length,
  inProgress: tasks.filter(t => t.status?.toLowerCase().includes('progress')).length
};

// An√°lisis por usuario
const tasksByUser = {};
const blockedTasks = [];

tasks.forEach(task => {
  const assignee = task.assignee || task.assignedTo || 'Sin asignar';

  if (!tasksByUser[assignee]) {
    tasksByUser[assignee] = {
      total: 0,
      pending: 0,
      completed: 0,
      blocked: 0
    };
  }

  tasksByUser[assignee].total++;

  if (task.status?.toLowerCase().includes('complet') || task.status?.toLowerCase().includes('done')) {
    tasksByUser[assignee].completed++;
  } else if (task.status?.toLowerCase().includes('blocked') || task.status?.toLowerCase().includes('bloqueada')) {
    tasksByUser[assignee].blocked++;
    blockedTasks.push({
      id: task.id,
      title: task.title,
      assignee: assignee,
      description: task.description
    });
  } else {
    tasksByUser[assignee].pending++;
  }
});

// Usuario con m√°s tareas pendientes
const userWithMostPending = Object.entries(tasksByUser)
  .sort(([,a], [,b]) => b.pending - a.pending)[0];

// Tareas recientes (√∫ltimas 5)
const recentTasks = tasks.slice(-5).map(task => ({
  id: task.id,
  title: task.title,
  status: task.status,
  assignee: task.assignee || 'Sin asignar'
}));

// Detectar tipo de pregunta para respuesta m√°s inteligente
const questionType = {
  isBlocked: userMessage.toLowerCase().includes('bloqueada') || userMessage.toLowerCase().includes('blocked'),
  isWho: userMessage.toLowerCase().includes('quien') || userMessage.toLowerCase().includes('who'),
  isSuggest: userMessage.toLowerCase().includes('suger') || userMessage.toLowerCase().includes('suggest'),
  isStatus: userMessage.toLowerCase().includes('estado') || userMessage.toLowerCase().includes('status'),
  isGeneral: userMessage.toLowerCase().includes('hola') || userMessage === '' || userMessage.toLowerCase().includes('help')
};

return {
  tasks,
  userMessage,
  stats,
  tasksByUser,
  blockedTasks,
  userWithMostPending,
  recentTasks,
  questionType,
  timestamp: new Date().toISOString()
};

