// Procesar respuesta de Groq y agregar funcionalidades adicionales
const groqResponse = $input.first().json;
const analysisData = $('Smart Task Analysis').first().json;

let aiMessage = '';

// Extraer mensaje de la respuesta de Groq
if (groqResponse.choices && groqResponse.choices[0] && groqResponse.choices[0].message) {
  aiMessage = groqResponse.choices[0].message.content;
} else {
  aiMessage = '‚ùå Error al procesar la respuesta de la IA. Int√©ntalo de nuevo.';
}

// Agregar sugerencias autom√°ticas inteligentes
let autoSuggestions = [];

// L√≥gica de sugerencias autom√°ticas basada en el an√°lisis
if (analysisData.stats.blocked > 0) {
  autoSuggestions.push(`üö® Atenci√≥n: Hay ${analysisData.stats.blocked} tareas bloqueadas que requieren intervenci√≥n`);
}

if (analysisData.stats.pending > analysisData.stats.completed && analysisData.stats.total > 3) {
  autoSuggestions.push(`üìä Las tareas pendientes (${analysisData.stats.pending}) superan a las completadas (${analysisData.stats.completed})`);
}

if (analysisData.userWithMostPending && analysisData.userWithMostPending[1].pending > 3) {
  autoSuggestions.push(`üë§ ${analysisData.userWithMostPending[0]} tiene sobrecarga de trabajo con ${analysisData.userWithMostPending[1].pending} tareas pendientes`);
}

if (analysisData.stats.total === 0) {
  autoSuggestions.push(`üéØ El proyecto est√° vac√≠o. ¬°Es hora de crear las primeras tareas!`);
}

// Si es una consulta general, agregar las sugerencias
if (analysisData.questionType.isGeneral || analysisData.questionType.isStatus) {
  if (autoSuggestions.length > 0) {
    aiMessage += `\n\nüí° **Recomendaciones autom√°ticas:**\n${autoSuggestions.map(s => `‚Ä¢ ${s}`).join('\n')}`;
  }

  // Agregar acciones r√°pidas
  aiMessage += `\n\nüîß **Acciones disponibles:**\n‚Ä¢ Preg√∫ntame "¬øqu√© tareas est√°n bloqueadas?"\n‚Ä¢ Preg√∫ntame "¬øqui√©n tiene m√°s tareas pendientes?"\n‚Ä¢ Dime "sugiere tareas" para obtener nuevas ideas\n‚Ä¢ Consulta el "estado del proyecto" en cualquier momento`;
}

// Agregar timestamp y metadata
const finalResponse = {
  message: aiMessage,
  suggestions: autoSuggestions,
  timestamp: new Date().toLocaleString('es-ES'),
  stats: {
    totalTasks: analysisData.stats.total,
    blockedTasks: analysisData.stats.blocked,
    pendingTasks: analysisData.stats.pending
  }
};

return finalResponse;
